import "bootstrap/dist/css/bootstrap.min.css";
import axios from "axios";
import {Modal, ModalBody, ModalFooter, ModalHeader } from 'reactstrap';
import { Component } from 'react';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faEdit, faTrashAlt } from "@fortawesome/free-solid-svg-icons";


const url = "http://localhost:8080/producto/listaProductos";

class App extends Component{
  state={
    data:[],
    modalInsertar: false,
  }

  modalInsertar=()=>{
    this.setState({modalInsertar:!this.state.modalInsertar});
  }

  peticionesGet=()=>{
    axios.get(url).then(response=>{
      this.setState({data: response.data});
    })
  }

  componentDidMount() {
    this.peticionesGet();
  }
  render(){
    return (
      <div className="App">
        <button type="button" className="btn btn-success" onClick={()=>this.modalInsertar()}>Agregar Producto</button>
        <table className="table">
          <thead>
            <tr>
              <th>Codigo</th>
              <th>Producto</th>
              <th>Marca</th>
              <th>Descripción</th>
              <th>Costo</th>
              <th>Precio</th>
              <th>Existencia</th>
              <th></th>
            </tr>
          </thead>
          <tbody>
          {this.state.data.map(producto=>{
            return(
              <tr>
                <td>{producto.id_producto}</td>
                <td>{producto.producto}</td>
                <td>{producto.id_marca}</td>
                <td>{producto.descripcion}</td>
                <td>{producto.precio_costo}</td>
                <td>{producto.precio_venta}</td>
                <td>{producto.existencia}</td>
                <td>
                  <button type="button" class="btn btn-primary"><FontAwesomeIcon icon={faEdit}/></button>
                  <button type="button" class="btn btn-danger"><FontAwesomeIcon icon={faTrashAlt}/></button>
                </td>
              </tr>
            )
          })}
          </tbody>
        </table>
        <Modal>
          <ModalHeader style={{display:'block'}}>
            <span style={{float:'right'}}>x</span>
          </ModalHeader>
          <ModalBody>
            <div className="form-group">
              <label htmlFor="id">ID</label>
              <input className="form-control" type="text" name="id_producto" id="id_producto" readOnly/>

              <label htmlFor="producto">Producto</label>
              <input className="form-control" type="text" name="producto" id="producto" />

              <label htmlFor="marca">Marca</label>
              <select className="form-control" name="marca" id="marca">
              <option value="0">-- Seleccione --</option>
                <option value="1">Logitech</option>
                <option value="2">Samsung</option>
              </select>

              <label htmlFor="descripcion">Descripción</label>
              <input className="form-control" type="text" name="descripcion" id="descripcion" />

              <label htmlFor="precio_costo">Costo</label>
              <input className="form-control" type="number" step="0.01" name="precio_costo" id="precio_costo" />

              <label htmlFor="precio_venta">Precio</label>
              <input className="form-control" type="number" step="0.01" name="precio_venta" id="precio_venta" />

              <label htmlFor="existencia">Existencia</label>
              <input className="form-control" type="number" name="existencia" id="existencia" />
            </div>
          </ModalBody>
          <ModalFooter>
            <button className="btn btn-success">
              Insertar
            </button>
            <button type="button" class="btn btn-light" onClick={()=>this.modalInsertar()}>Cancelar</button>
          </ModalFooter>
        </Modal>
      </div>
    );
  }
}

export default App;
