import "bootstrap/dist/css/bootstrap.min.css";
import axios from "axios";
import {Modal, ModalBody, ModalFooter, ModalHeader } from 'reactstrap';
import { Component } from 'react';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faEdit, faTrashAlt } from "@fortawesome/free-solid-svg-icons";


const url = "http://localhost:8080/producto/listaProductos";
const urlPost = "http://localhost:8080/producto/crearProducto";

class App extends Component{
  state={
    data:[],
    modalInsertar: false,
    form:{
      producto:'',
      id_marca:0,
      descripcion:'',
      precio_costo:0,
      precio_venta:0,
      existencia:0
    }
  }

  modalInsertar=()=>{
    this.setState({modalInsertar:!this.state.modalInsertar});
  }

  peticionesGet=()=>{
    axios.get(url).then(response=>{
      this.setState({data: response.data});
    })
  }

  handleChange=async (e)=>{
    e.persist();
    await this.setState({
      form:{
        ...this.state.form,
        [e.target.name]: e.target.value
      }
    });
    console.log(this.state.form);
  }

  peticionPost=async ()=>{
    delete this.state.form.id_producto;
    await axios.post(urlPost,this.state.form).then(response=>{
      this.modalInsertar();
      this.peticionesGet();
    }).catch((error)=>{
      console.log(error);
    });
  }

  componentDidMount() {
    this.peticionesGet();
  }
  render(){
    const {form}=this.state;
    return (
      <div className="App">
        <button type="button" className="btn btn-success" onClick={()=>this.modalInsertar()}>Agregar Producto</button>
        <table className="table">
          <thead>
            <tr>
              <th>Codigo</th>
              <th>Producto</th>
              <th>Marca</th>
              <th>Descripción</th>
              <th>Costo</th>
              <th>Precio</th>
              <th>Existencia</th>
              <th></th>
            </tr>
          </thead>
          <tbody>
          {this.state.data.map(producto=>{
            return(
              <tr>
                <td>{producto.id_producto}</td>
                <td>{producto.producto}</td>
                <td>{producto.id_marca}</td>
                <td>{producto.descripcion}</td>
                <td>{producto.precio_costo}</td>
                <td>{producto.precio_venta}</td>
                <td>{producto.existencia}</td>
                <td>
                  <button type="button" className="btn btn-primary"><FontAwesomeIcon icon={faEdit}/></button>
                  <button type="button" className="btn btn-danger"><FontAwesomeIcon icon={faTrashAlt}/></button>
                </td>
              </tr>
            )
          })}
          </tbody>
        </table>
        <Modal isOpen={this.state.modalInsertar}>
          <ModalHeader style={{display:'block'}}>
            <span style={{float:'right'}}>x</span>
          </ModalHeader>
          <ModalBody>
            <div className="form-group">
              <label htmlFor="id">ID</label>
              <input className="form-control" type="text" name="id_producto" id="id_producto" readOnly value={this.state.data.length+1} />

              <label htmlFor="producto">Producto</label>
              <input className="form-control" type="text" name="producto" id="producto" onChange={this.handleChange} value={form.producto} />

              <label htmlFor="marca">Marca</label>
              <select className="form-control" name="id_marca" id="id_marca" onChange={this.handleChange}>
              <option value="0">-- Seleccione --</option>
                <option value="1">Logitech</option>
                <option value="2">Samsung</option>
              </select>

              <label htmlFor="descripcion">Descripción</label>
              <input className="form-control" type="text" name="descripcion" id="descripcion" onChange={this.handleChange} value={form.descripcion}/>

              <label htmlFor="precio_costo">Costo</label>
              <input className="form-control" type="number" step="0.01" name="precio_costo" id="precio_costo" onChange={this.handleChange} value={form.precio_costo} />

              <label htmlFor="precio_venta">Precio</label>
              <input className="form-control" type="number" step="0.01" name="precio_venta" id="precio_venta" onChange={this.handleChange} value={form.precio_venta} />

              <label htmlFor="existencia">Existencia</label>
              <input className="form-control" type="number" name="existencia" id="existencia" onChange={this.handleChange} value={form.existencia} />
            </div>
          </ModalBody>
          <ModalFooter>
            <button className="btn btn-success" onClick={()=>this.peticionPost()}>
              Insertar
            </button>
            <button type="button" className="btn btn-dark" onClick={()=>this.modalInsertar()}>Cancelar</button>
          </ModalFooter>
        </Modal>
      </div>
    );
  }
}

export default App;
