import "bootstrap/dist/css/bootstrap.min.css";
import axios from "axios";
import {Modal, ModalBody, ModalFooter, ModalHeader } from 'reactstrap';
import { Component } from 'react';


const url = "http://localhost:8080/producto/listaProductos";

class App extends Component{
  state={
    data:[]
  }

  peticionesGet=()=>{
    axios.get(url).then(response=>{
      this.setState({data: response.data});
    })
  }

  componentDidMount() {
    this.peticionesGet();
  }
  render(){
    return (
      <div className="App">
        <button type="button" className="btn btn-success">Agregar Producto</button>
        <table className="table">
          <thead>
            <tr>
              <th>Codigo</th>
              <th>Producto</th>
              <th>Marca</th>
              <th>Descripci√≥n</th>
              <th>Costo</th>
              <th>Venta</th>
              <th>Existencia</th>
              <th></th>
            </tr>
          </thead>
          <tbody>
          {this.state.data.map(producto=>{
            return(
              <tr>
                <td>{producto.id_producto}</td>
                <td>{producto.producto}</td>
                <td>{producto.id_marca}</td>
                <td>{producto.descripcion}</td>
                <td>{producto.precio_costo}</td>
                <td>{producto.precio_venta}</td>
                <td>{producto.existencia}</td>
                <td>
                  <button type="button" class="btn btn-primary"><FontAwesomeIcon icon={faEdit}/></button>
                  <button type="button" class="btn btn-danger"><FontAwesomeIcon icon={faTrashAlt}/></button>
                </td>
              </tr>
            )
          })}
          </tbody>
        </table>

      </div>
    );
  }
}

export default App;
